set GLOBAL time_zone = '-3:00';

-- Check DB
DROP DATABASE IF EXISTS vk;
CREATE DATABASE vk;
USE vk;

DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `firstname` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `lastname` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Фамилия',
  `email` varchar(120) COLLATE utf8_unicode_ci DEFAULT NULL,
  `phone` int(20) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `phone` (`phone`),
  UNIQUE KEY `email` (`email`),
  KEY `users_phone_idx` (`phone`),
  KEY `users_firstname_lastname_idx` (`firstname`),
  KEY `users_lastname_idx` (`lastname`)
) COMMENT='Пользователи';


DROP TABLE IF EXISTS `communities`;
CREATE TABLE `communities` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(150) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `communities_name_idx` (`name`)
) COMMENT='Сообщества';


DROP TABLE IF EXISTS `friend_requests`;
CREATE TABLE `friend_requests` (
  `initiator_user_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `target_user_id` bigint(20) unsigned NOT NULL,
  `status` enum('requested','approved','unfriended','declined') COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  `requested_at` datetime DEFAULT current_timestamp(),
  `confirmed_at` datetime DEFAULT NULL,
  PRIMARY KEY (`initiator_user_id`,`target_user_id`),
  UNIQUE KEY `initiator_user_id` (`initiator_user_id`),
  KEY `initiator_user_id_2` (`initiator_user_id`),
  KEY `target_user_id` (`target_user_id`),
  FOREIGN KEY (`initiator_user_id`) REFERENCES `users` (`id`),
  FOREIGN KEY (`target_user_id`) REFERENCES `users` (`id`)
) ;



DROP TABLE IF EXISTS `media_types`;
CREATE TABLE `media_types` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  `updated_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  PRIMARY KEY (`id`)
) ;


DROP TABLE IF EXISTS `media`;
CREATE TABLE `media` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `media_type_id` bigint(20) unsigned NOT NULL,
  `user_id` bigint(20) unsigned NOT NULL,
  `body` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `filename` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `size` int(11) DEFAULT NULL,
  `metadata` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`metadata`)),
  `created_at` datetime DEFAULT current_timestamp(),
  `updated_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `media_type_id` (`media_type_id`),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`),
  FOREIGN KEY (`media_type_id`) REFERENCES `media_types` (`id`)
) ;


DROP TABLE IF EXISTS `photo_albums`;
CREATE TABLE `photo_albums` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `user_id` bigint(20) unsigned DEFAULT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  `update_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`),
  KEY `user_id` (`user_id`),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)
);



DROP TABLE IF EXISTS `photos`;
CREATE TABLE `photos` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `album_id` bigint(20) unsigned NOT NULL,
  `media_id` bigint(20) unsigned NOT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  `update_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `album_id` (`album_id`),
  KEY `media_id` (`media_id`),
  FOREIGN KEY (`album_id`) REFERENCES `photo_albums` (`id`),
  FOREIGN KEY (`media_id`) REFERENCES `media` (`id`)
);


DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `author_id` bigint(20) unsigned DEFAULT NULL,
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `body_text` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  `updated_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `connected_photo_id` bigint(20) unsigned DEFAULT NULL,
  `connected_media_id` bigint(20) unsigned DEFAULT NULL,
  `comments_id` bigint(20) unsigned DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `name` (`name`),
  KEY `author_id` (`author_id`),
  KEY `connected_photo_id` (`connected_photo_id`),
  KEY `connected_media_id` (`connected_media_id`),
  FOREIGN KEY (`author_id`) REFERENCES `users` (`id`),
  FOREIGN KEY (`connected_photo_id`) REFERENCES `photos` (`id`),
  FOREIGN KEY (`connected_media_id`) REFERENCES `media` (`id`)
) COMMENT='Посты пользователей';


DROP TABLE IF EXISTS `messages`;
CREATE TABLE `messages` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `from_user_id` bigint(20) unsigned NOT NULL,
  `to_user_id` bigint(20) unsigned NOT NULL,
  `body` text COLLATE utf8_unicode_ci NOT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `messages_from_user_id` (`from_user_id`),
  KEY `messages_to_user_id` (`to_user_id`),
  KEY `messages_to_created_at` (`created_at`),
  FOREIGN KEY (`from_user_id`) REFERENCES `users` (`id`),
  FOREIGN KEY (`to_user_id`) REFERENCES `users` (`id`)
);


DROP TABLE IF EXISTS `profiles`;
CREATE TABLE `profiles` (
  `user_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `gender` enum('male','female') COLLATE utf8_unicode_ci NOT NULL,
  `birthday` date NOT NULL,
  `photo_id` bigint(20) unsigned DEFAULT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  `updated_at` datetime DEFAULT NULL ON UPDATE current_timestamp(),
  `hometown` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`user_id`),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON UPDATE CASCADE
) COMMENT='Информация о пользователе';


DROP TABLE IF EXISTS `users_communities`;
CREATE TABLE `users_communities` (
  `user_id` bigint(20) unsigned NOT NULL,
  `community_id` bigint(20) unsigned NOT NULL,
  `created_at` datetime DEFAULT current_timestamp(),
  PRIMARY KEY (`user_id`,`community_id`),
  KEY `community_id` (`community_id`),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`),
  FOREIGN KEY (`community_id`) REFERENCES `communities` (`id`)
);


DROP TABLE IF EXISTS `likes_dislakes_user`;
CREATE TABLE `likes_dislakes_user` (
  `id` SERIAL PRIMARY KEY,
  `state` enum('like','dislike'),
  `user_id_init` bigint(20) unsigned NOT NULL COMMENT 'От кого адресован лайк/дизлайк',
  `user_id_for_like` bigint(20) unsigned NOT NULL COMMENT  'лайк/дизлайк пользователю',
  `created_at` datetime DEFAULT current_timestamp(),
  `update_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  UNIQUE KEY (`user_id_init`,`user_id_for_like`),
  FOREIGN KEY (`user_id_for_like`) REFERENCES `users` (`id`),
  FOREIGN KEY (`user_id_init`) REFERENCES `users` (`id`)
);


DROP TABLE IF EXISTS `likes_post_photo`;
CREATE TABLE `likes_post_photo` (
  `id` SERIAL PRIMARY KEY,
  `state` enum('like','dislike'),
  `user_id_init` bigint(20) unsigned NOT NULL COMMENT 'От кого адресован лайк/дизлайк',
  `photo_id` bigint(20) unsigned NOT NULL ,
  `created_at` datetime DEFAULT current_timestamp(),
  `update_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  UNIQUE KEY (`user_id_init`,`photo_id`),
  FOREIGN KEY (`user_id_init`) REFERENCES `users` (`id`),
  FOREIGN KEY (`photo_id`) REFERENCES `photos` (`id`)
);


DROP TABLE IF EXISTS `likes_dislakes_media`;
CREATE TABLE `likes_dislakes_media` (
  `id` SERIAL PRIMARY KEY,
  `state` enum('like','dislike'),
  `user_id_init` bigint(20) unsigned NOT NULL COMMENT 'От кого адресован лайк/дизлайк',
  `media_id` bigint(20) unsigned NOT NULL ,
  `created_at` datetime DEFAULT current_timestamp(),
  `update_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  UNIQUE KEY (`user_id_init`,`media_id`),
  FOREIGN KEY (`user_id_init`) REFERENCES `users` (`id`),
  FOREIGN KEY (`media_id`) REFERENCES `media` (`id`)
);


DROP TABLE IF EXISTS `likes_dislakes_post`;
CREATE TABLE `likes_dislakes_post` (
  `id` SERIAL PRIMARY KEY,
  `state` enum('like','dislike'),
  `user_id_init` bigint(20) unsigned NOT NULL COMMENT 'От кого адресован лайк/дизлайк',
  `post_id` bigint(20) unsigned NOT NULL ,
  `created_at` datetime DEFAULT current_timestamp(),
  `update_at` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  UNIQUE KEY (`user_id_init`, `post_id`),
  FOREIGN KEY (`user_id_init`) REFERENCES `users` (`id`),
  FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`)
);


